	scanf("%d",&n);
	for(int i=1;i<=n;i++) scanf("%d",&a[i]);
	int ans=0,m=0;
	for(int i=1;i<=n;i++){
		if(a[i]!=a[i-1]) a[++m]=a[i];
	}
	for(int i=1;i<=m;i++){
		if(!mp[a[i]]){
			mp[a[i]]=1;
			ans++;
		}
		else{
			mp.clear();
			mp[a[i]]=1;
			mp[a[i-1]]=1;
		}
	}
	printf("%d",ans);
}
